def Forecast(index, duration=604800, shinyIndex=None):
    """duration in seconds"""
    if shinyIndex is None:
        shinyIndex = SHINY_INDEX
    adv = duration * AVERAGE_ADVANCES_PER_SECOND
    seeds = filter((lambda x: x > index and x < (index + adv)), shinyIndex)
    i = 0
    for seed in seeds:
        print(time(seed-index))
        i += 1
    print("total:", i)


